function tbutton1()
	if t1 ~= nil then
	target = t1
	cecho("<white>Target set to <firebrick>"..target.."<white>.\n\n")
	GUI.TargetInput:echo("<center>"..target)

	table.save(PkgPath.."vt.lua", vt)
else
	cecho("<white>You do not have a target assigned to F1. Use the 't1' command to add an alternate target.\n\n")
	end
end

