function hide_map_on_click()
	GUI.WorldMap:resize(1,1)
	GUI.WorldMap:hide()
	mapcount = 0
end

function worldmap_button()
	if mapcount == 0 then
	GUI.WorldMap:resize(1024,768)
	GUI.WorldMap:show()
	mapcount = 1
	else
	GUI.WorldMap:hide()
	mapcount = 0
	end

end

